import "./Room.css";


function Room() {

    const [state, setStatus] = useState(true);


    function changeStatus() {

        setStatus(currentState =>  currentState? false : true)

        {/*setStatus(!currentState) dann macht er automatisch toggle*/ }

    }




    return (
        <div className={`room ${state? "lit": "dark"}`}>


            <p>The room is {state? "lit": "dark"}</p>
            <br/>
            <button onClick={changeStatus}>lightswitch</button>

        </div>
    )
}

export default Room
